mapper.select:
 	SELECT 
 	if(loginCount=null, 0, loginCount) as logincount
	FROM emp where empno = #{empno}

SELECT empno, statusCode
FROM emp
WHERE empno = #{empno}

mapper.update:
UPDATE emp
SET loginCount = IF(loginCount=null, 0, loginCount) + 1
WHERE empno = #{empno};

UPDATE emp 
SET statusCode = 40, enabled = 0
WHERE empno = #{empno}

UPDATE emp 
SET loginCount = 0
WHERE empno = #{empno}
